import React, { useState } from 'react';

const dummyProperties = [
    {
      id: 1,
      name: 'Luxury Apartment',
      location: 'New York, NY',
      price: 3000,
      bedrooms: 2,
      amenities: ['Swimming Pool', 'Gym', 'Parking']
    },
    {
      id: 2,
      name: 'Cozy Cottage',
      location: 'Los Angeles, CA',
      price: 2000,
      bedrooms: 1,
      amenities: ['Garden', 'Fireplace']
    },
    {
      id: 3,
      name: 'Modern Condo',
      location: 'Chicago, IL',
      price: 2500,
      bedrooms: 2,
      amenities: ['Balcony', 'Fitness Center']
    },
    {
      id: 4,
      name: 'Downtown Loft',
      location: 'San Francisco, CA',
      price: 4000,
      bedrooms: 3,
      amenities: ['City View', 'Security']
    },
    {
      id: 5,
      name: 'Lake House',
      location: 'Seattle, WA',
      price: 3500,
      bedrooms: 2,
      amenities: ['Lakefront', 'Boat Dock']
    }
  ];

const PropertyFilter = ({ properties }) => {
  // State variables for properties and filter criteria
  const [filteredProperties, setFilteredProperties] = useState(dummyProperties);
  const [filters, setFilters] = useState({
    location: '',
    minPrice: '',
    maxPrice: '',
    minBedrooms: '',
    amenities: ''
  });

  // Function to handle filter change
  const handleFilterChange = e => {
    const { name, value } = e.target;
    setFilters(prevFilters => ({
      ...prevFilters,
      [name]: value
    }));
  };

  // Function to apply filters
  const applyFilters = () => {
    let filtered = [...properties];

    // Filter by location
    if (filters.location) {
      filtered = filtered.filter(property =>
        property.location.toLowerCase().includes(filters.location.toLowerCase())
      );
    }

    // Filter by price range
    if (filters.minPrice && filters.maxPrice) {
      filtered = filtered.filter(
        property =>
          property.price >= parseInt(filters.minPrice) &&
          property.price <= parseInt(filters.maxPrice)
      );
    }

    // Filter by number of bedrooms
    if (filters.minBedrooms) {
      filtered = filtered.filter(
        property =>
          property.bedrooms >= parseInt(filters.minBedrooms)
      );
    }

    // Filter by amenities
    if (filters.amenities) {
      filtered = filtered.filter(property =>
        property.amenities.includes(filters.amenities)
      );
    }

    // Update filtered properties state
    setFilteredProperties(filtered);
  };

  // Function to clear filters
  const clearFilters = () => {
    setFilters({
      location: '',
      minPrice: '',
      maxPrice: '',
      minBedrooms: '',
      amenities: ''
    });
    setFilteredProperties(properties);
  };

  return (
    <div>
      <h2>Property Filters</h2>
      <label>
        Location:
        <input
          type="text"
          name="location"
          value={filters.location}
          onChange={handleFilterChange}
        />
      </label>
      <label>
        Min Price:
        <input
          type="number"
          name="minPrice"
          value={filters.minPrice}
          onChange={handleFilterChange}
        />
      </label>
      <label>
        Max Price:
        <input
          type="number"
          name="maxPrice"
          value={filters.maxPrice}
          onChange={handleFilterChange}
        />
      </label>
      <label>
        Min Bedrooms:
        <input
          type="number"
          name="minBedrooms"
          value={filters.minBedrooms}
          onChange={handleFilterChange}
        />
      </label>
      <label>
        Amenities:
        <input
          type="text"
          name="amenities"
          value={filters.amenities}
          onChange={handleFilterChange}
        />
      </label>
      <button onClick={applyFilters}>Apply Filters</button>
      <button onClick={clearFilters}>Clear Filters</button>
      <ul>
        {filteredProperties.map(property => (
          <li key={property.id}>
            <p>Name: {property.name}</p>
            <p>Location: {property.location}</p>
            <p>Price: {property.price}</p>
            <p>Bedrooms: {property.bedrooms}</p>
            <p>Amenities: {property.amenities.join(', ')}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PropertyFilter;
