import React, { useState } from 'react';

const Rooms = () => {
  // State to store properties
  const [properties, setProperties] = useState([]);
  // State to store input field values
  const [propertyInput, setPropertyInput] = useState({
    name: '',
    location: ''
  });

  // Function to handle input change
  const handleInputChange = e => {
    const { name, value } = e.target;
    setPropertyInput(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Function to handle property submission
  const handleSubmit = e => {
    e.preventDefault();
    const newProperty = {
      name: propertyInput.name,
      location: propertyInput.location
    };
    setProperties(prevProperties => [...prevProperties, newProperty]);
    setPropertyInput({ name: '', location: '' });
  };

  // Function to sort properties by location
  const handleSortByLocation = () => {
    const sortedProperties = [...properties].sort((a, b) =>
      a.location.localeCompare(b.location)
    );
    setProperties(sortedProperties);
  };

  return (
    <div>
      <h2>Property Input Form</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Property Name:
          <input
            type="text"
            name="name"
            value={propertyInput.name}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Location:
          <input
            type="text"
            name="location"
            value={propertyInput.location}
            onChange={handleInputChange}
          />
        </label>
        <button type="submit">Add Property</button>
      </form>
      <button onClick={handleSortByLocation}>Sort by Location</button>
      <ul>
        {properties.map((property, index) => (
          <li key={index}>
            <p>Name: {property.name}</p>
            <p>Location: {property.location}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Rooms;
