import { useState,useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import img1 from "../../images/img1.jpg"
import img2 from "../../images/img2.jpg"
import img3 from "../../images/img3.jpg"
import img4 from "../../images/img4.jpg"
import Header from "../Header"
import './index.css'

const Home = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, 
    autoplaySpeed: 3000,
    pauseOnHover: false
  }

  useEffect(() => {
    const fetchData = async () => {
      const hotelsOptions = {
        method: 'GET',
        url: 'https://booking-com15.p.rapidapi.com/api/v1/hotels/searchDestination',
        params: { query: 'man' },
        headers: {
          'X-RapidAPI-Key': 'YOUR_BOOKING_COM_KEY',
          'X-RapidAPI-Host': 'booking-com15.p.rapidapi.com'
        }
      };

      const flightsOptions = {
        method: 'GET',
        headers: {
          'X-RapidAPI-Key': 'YOUR_SKY_SCRAPPER_KEY',
          'X-RapidAPI-Host': 'sky-scrapper.p.rapidapi.com'
        }
      };

      try {
        // Fetch hotels
        const hotelsResponse = await axios.request(hotelsOptions);
        console.log(hotelsResponse.data);

        // Fetch nearby airports
        const url = 'https://sky-scrapper.p.rapidapi.com/api/v1/flights/getNearByAirports?lat=19.242218017578125&lng=72.85846156046128';
        const flightsResponse = await fetch(url, flightsOptions);
        const flightsResult = await flightsResponse.text();
        console.log(flightsResult);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData(); // Call the function to fetch data when the component mounts
  }, []); 
  

  return (
    <>
   <Header/>
    <div className='home-container'>
    <div className="slider-container">
      <Slider {...settings}>
        <div>
          <img src={img1} alt="img"/>
        </div>
        <div>
        <img src={img2} alt="img"/>
        </div>
        <div>
        <img src={img3} alt="img"/>
        </div>
        <div>
        <img src={img4} alt="img"/>
        </div>
      </Slider>
      <div className='slide-text-container'>
        <p>Simple - Unique - Friendly</p>
        <h1>Make Yourself At Home <br/>In Our <span>Hotel.</span></h1>
      </div>
    </div>

    </div>
    </>
  )
}

export default Home

