import { useState,useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import img1 from "../../images/img1.jpg"
import img2 from "../../images/img2.jpg"
import img3 from "../../images/img3.jpg"
import img4 from "../../images/img4.jpg"
import Header from "../Header"
import Rooms from "../Rooms"
import {Puff} from 'react-loader-spinner'
import './index.css'

const apiStatusConstants = {
  initial: 'INITIAL',
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}


const Home = () => {
  const [values,setValues] = useState([])
  const [apiStatus,setApiStatus] = useState({apiStatus:apiStatusConstants.initial})

  const settings = {
    dots: false,
    infinite: true,
    speed: 2000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, 
    autoplaySpeed: 3000,
    pauseOnHover: false
  }

  useEffect(() => {
    setApiStatus({apiStatus:apiStatusConstants.inProgress})
    const fetchData = async () => {

        const userDetails = {
            username: "satish",
            password: "satish123",
        };

        const url = 'https://booking-com13.p.rapidapi.com/stays/properties/list-v2?location=Mumbai%2C%20Maharashtra%2C%20India&checkin_date=2024-04-25&checkout_date=2024-04-28&language_code=en-us&currency_code=INR&property_type=Hotels';

        const options = {
            method: 'GET',
            headers: {
              'X-RapidAPI-Key': 'c090189d3fmsh5c804c68ae207dap17abe2jsn36f580be562f',
              'X-RapidAPI-Host': 'booking-com13.p.rapidapi.com'
              }
           
        };

        try {
            const response = await fetch(url, options);
            if (response.ok) {
                const data = await response.json();
               const ss =  data.data
               setValues(ss)
               setApiStatus({apiStatus:apiStatusConstants.success})
               
            } else {
                const data = await response.json();
                
            }
        } catch (error) {
            console.error('Error occurred while fetching:', error)
        }
      };
  
      fetchData();
    }, [])

   const renderLoadingView = () => (
      <div className="products-loader-container">
        <Puff type="ThreeDots" color="#0b69ff" height="50" width="50" />
      </div>
    )

    const renderPropertiesHomePage = () => {
       switch(apiStatus){
            case apiStatusConstants.success:
        return this.renderPrimeDealsList()
      case apiStatusConstants.failure:
        return this.renderPrimeDealsFailureView()
      case apiStatusConstants.inProgress:
        return this.renderLoadingView()
      default:
        return null
       }
    }

  return (
    <>
   <Header/>
   <div>
    
    </div>
    </>
  )
}

export default Home

