import { useState,useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import img1 from "../../images/img1.jpg"
import img2 from "../../images/img2.jpg"
import img3 from "../../images/img3.jpg"
import img4 from "../../images/img4.jpg"
import Header from "../Header"
import './index.css'

const Home = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 2000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, 
    autoplaySpeed: 3000,
    pauseOnHover: false
  }

  useEffect(() => {
    const fetchData = async () => {

        /* const url = 'https://booking-com13.p.rapidapi.com/stays/properties/list-v2?location=Mumbai%2C%20Maharashtra%2C%20India&checkin_date=2024-04-20&checkout_date=2024-04-28&language_code=en-us&currency_code=INR&rooms=4&property_type=Hotels';
const options = {
	method: 'GET',
	headers: {
		'X-RapidAPI-Key': '7749cec638msh006cc43cfd52ee1p1eeb23jsn17467ce59857',
		'X-RapidAPI-Host': 'booking-com13.p.rapidapi.com'
	}
};

try {
	const response = await fetch(url, options);
	const result = await response.text();
	console.log(result);
} catch (error) {
	console.error(error);
}*/
        const userDetails = {
            username: "satish",
            password: "satish123",
        };

        const url = 'https://booking-com13.p.rapidapi.com/stays/properties/list-v2?location=Mumbai%2C%20Maharashtra%2C%20India&checkin_date=2024-04-20&checkout_date=2024-04-28&language_code=en-us&currency_code=INR&property_type=Hotels';

        const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': '7749cec638msh006cc43cfd52ee1p1eeb23jsn17467ce59857',
                'X-RapidAPI-Host': 'booking-com13.p.rapidapi.com'
              }
           
        };

        try {
            const response = await fetch(url, options);
            if (response.ok) {
                const data = await response.json();
                console.log(data)
            } else {
                const data = await response.json();
                console.log(data)
            }
        } catch (error) {
            console.error('Error occurred while fetching:', error)
        }
      };
  
      fetchData();
    }, [])

  return (
    <>
   <Header/>
    <div className='home-container'>
    <div className="slider-container">
      <Slider {...settings}>
        <div>
          <img src={img1} alt="img"/>
        </div>
        <div>
        <img src={img2} alt="img"/>
        </div>
        <div>
        <img src={img3} alt="img"/>
        </div>
        <div>
        <img src={img4} alt="img"/>
        </div>
      </Slider>
      <div className='slide-text-container'>
        <p>Simple - Unique - Friendly</p>
        <h1>Make Yourself At Home <br/>In Our <span>Hotel.</span></h1>
      </div>
      <form>
        <label>
            Arrival Date
        </label>
        <input type='date'/>
      </form>
    </div>

    </div>
    </>
  )
}

export default Home

